body, html {
    height: 100%;
    margin: 0;
    font-family: Arial, Helvetica, sans-serif;
}

.hero-image {
    background-image: url("https://i.pinimg.com/originals/90/40/46/9040464dc2da611ef8ada5afedb00773.gif");
    height: 100%;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}

/**********************************
    Character Grid
**********************************/

.grid-container {
    display: grid;
    grid-template-columns: auto auto auto auto auto;
    grid-row-gap: 20px;
    grid-column-gap: 5px;
    padding: 20px;
    padding-right: 1100px;
    padding-top: 15px;
    overflow: hidden;
}

.grid-container>div {
    padding: 10px;
}

#split {
    height: 100%;
    width: 50%;
    position: fixed;
    z-index: 1;
    top: 0;
    overflow-x: hidden;
    padding-top: 20px;
}

/***************************************************
            Image borders and animation
***************************************************/

.button {
    content: "P1";
    padding: 15px;
    cursor: pointer;
    outline: none;
    border: none;
    border-radius: 15px;
    box-shadow: 0 9px #999;
    height: 100px;
    width: 100px;
    background-color: lightgray;
    transform: skewY(-10deg);
}

.button:hover {
    background-color: #3e8e41;
}

.button:active {
    border: 5px solid green;
    box-shadow: 0 5px #666;
    transform: translateY(4px);
}

/********************************/

#characters {
    display: none;
    left: 55%;
    position: absolute;
    top: 85px;
    height: 550px;
    width: 550px;
    transform: skewY(-10deg);
    background-size: contain;
    border: 5px solid gray;
    font-size: 25px;
    color: white;
}

#charactersname {
    left: 55%;
    position: absolute;
    top: 85px;
    font-size: 35px;
    transform: skewY(-10deg);
    color: white;
    text-shadow: 2px 2px 5px black;
    width: 150px;
    background-color: aqua;
    text-align: center;
}

#charselect {
    color: white;
    font-size: 25px;
    bottom: 120px;
    position: absolute;
    left: 75%;
    text-shadow: 2px 2px 5px black;
    transform: skewY(-10deg);
    background-color: red;
}

#charactersubmit {
    left: 85%;
    position: absolute;
    top: 720px;
    font-size: 25px;
    color: white;
    text-shadow: 2px 2px 5px black;
    width: 150px;
    text-align: center;
    border-radius: 12px;
}


